(function( exports, $ ) {
    var api = sedApp.editor;

    $( function() {

        api.shortcodeCreate.sed_google_map = function( id , param ){

            param.context.setting_base_url = SED_WP_INCLUDES_URL;
            param.context.setting_module_url = SED_GOOGLE_MAP_MODULES_URL;
            param.context.setting_url = SED_GOOGLE_MAP_MODULES_URL + '/google-map/sed-gmap-custom.php?api_key=' + SED_GOOGLE_API_KEY;

            var js_settings_str = "";
            $.each( param.context , function( key , value ){
            	if( key.substr(0,7) == 'setting' ){
                    var key_setting = key.substr(8);
                    js_settings_str += "data-gmap-" + key_setting.replace(/\_/gi , "-") + '=\"' + value + '\" ';
            	}

            });
            param.context.item_settings = js_settings_str;

            return param;
        };

    });


}(sedApp, jQuery));